mapper.select:
  	select o.order_no, product_img, product_name,product_price, detail_cnt, status_code, detail_price, payment_code, cust_name, cust_phone, cust_addr1, cust_addr2, cust_addr3 
	from customer c, product p, customerOrder_detail d, customer_order o 
	where o.cust_id=c.cust_id and d.product_no=p.product_no and o.order_no=d.order_no and o.order_no=#{order_no}
